import React, {useRef} from 'react';
import {Canvas} from '@react-three/fiber';
import './App.css';
import {Cube} from "./components/cube";
import {OrbitControls} from '@react-three/drei';

function App() {


    return (
      <div id="canvas-container" style={{width: "100%", height:"100vh"}}>
        <Canvas style={{width: "100%", height:"100%"}}>
            <OrbitControls addEventListener={undefined} hasEventListener={undefined} removeEventListener={undefined} dispatchEvent={undefined}/>
            <ambientLight intensity={0.5}/>
            <spotLight position={[]}/>
            <Cube color="yellow" x={1} y={1} z={1} position={[-2,0,0]}/>
            <Cube color="green" x={1} y={1} z={1} position={[2,0,0]}/>
        </Canvas>
      </div>
  );
}

export default App;
