import React from 'react';
import {Canvas} from '@react-three/fiber';
import './App.css';
import {Cube} from "./components/cube";
import {OrbitControls, TransformControls} from '@react-three/drei';
import {Sfera} from "./components/sfera";
import {SolarSystem} from "./components/solarSystem";
import {Rotation} from "./components/rotation";

function App() {

    const solarSystemFigli: JSX.Element[] = [
        <Sfera radius={1} widthSegments={6} heightSegments={6} color="yellow" position={[-2,0,0]} />,
        <Sfera radius={1} widthSegments={6} heightSegments={6} color="red" position={[2,0,0]} />
    ]

    const solarSystem = <SolarSystem solarSystemFigli={solarSystemFigli}/>
    return (
      <div id="canvas-container" style={{width: "100%", height:"100vh", backgroundColor:"black"}}>
        <Canvas style={{width: "100%", height:"100%"}}>
            <ambientLight/>
            <pointLight position={[20, 20, 20]} />
            <TransformControls type={undefined} isGroup={undefined} id={undefined} uuid={undefined} name={undefined} parent={undefined} modelViewMatrix={false} normalMatrix={undefined} matrixWorld={undefined} matrixAutoUpdate={undefined} matrixWorldNeedsUpdate={undefined} castShadow={undefined} receiveShadow={undefined} frustumCulled={undefined} renderOrder={undefined} animations={undefined} userData={undefined} customDepthMaterial={undefined} customDistanceMaterial={undefined} isObject3D={undefined} onBeforeRender={undefined} onAfterRender={undefined} applyMatrix4={undefined} applyQuaternion={undefined} setRotationFromAxisAngle={undefined} setRotationFromEuler={undefined} setRotationFromMatrix={undefined} setRotationFromQuaternion={undefined} rotateOnAxis={undefined} rotateOnWorldAxis={undefined} rotateX={undefined} rotateY={undefined} rotateZ={undefined} translateOnAxis={undefined} translateX={undefined} translateY={undefined} translateZ={undefined} localToWorld={undefined} worldToLocal={undefined} lookAt={undefined} add={undefined} remove={undefined} clear={undefined} getObjectById={undefined} getObjectByName={undefined} getObjectByProperty={undefined} getWorldPosition={undefined} getWorldQuaternion={undefined} getWorldScale={undefined} getWorldDirection={undefined} raycast={undefined} traverse={undefined} traverseVisible={undefined} traverseAncestors={undefined} updateMatrix={undefined} updateWorldMatrix={undefined} toJSON={undefined} clone={undefined} copy={undefined} addEventListener={undefined} hasEventListener={undefined} removeEventListener={undefined} dispatchEvent={undefined}>
                <Sfera radius={1} widthSegments={6} heightSegments={6} color="red" position={[2,0,0]} />
            </TransformControls>
        </Canvas>
      </div>
  );
}

export default App;
