import React from 'react';
import {ProSidebar, Menu, MenuItem, SubMenu, SidebarHeader} from 'react-pro-sidebar';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCube } from '@fortawesome/free-solid-svg-icons'
import 'react-pro-sidebar/dist/css/styles.css';
import {MyCanvas} from "./canvas/MyCanvas";

interface SideBarProps {
}

export const SideBar: React.FC<SideBarProps> = () => {
    return(
        <ProSidebar style={{height: "100vh"}}>
            <SidebarHeader>
                <div className="row">
                    <div className="column" style={{width: "18%", float:"left"}}>
                        <h3>Editor</h3>
                    </div>
                    <div className="column" style={{width: "82%", float:"left"}}>
                        <FontAwesomeIcon icon={faCube} size="lg"/>
                    </div>

                </div>


            </SidebarHeader>
            <Menu>
                <SubMenu key={1} title="Components" icon={<FontAwesomeIcon icon={faCube}/>}>
                    <MenuItem>Cube</MenuItem>
                    <MenuItem>Sphere</MenuItem>
                </SubMenu>
            </Menu>
        </ProSidebar>
    )

}
