import React, {useRef} from 'react';
import {Canvas, Camera, useFrame} from '@react-three/fiber';
import './App.css';
import {Cube} from "./components/cube";

function App() {
    const myCube = useRef();
    useFrame(({ clock }) => {
        myCube.current.rotation.x = clock.getElapsedTime()
    })

    return (
      <div id="canvas-container">
        <Canvas>
            <Cube x={2} y={2} z={2} color="red" ref={myCube}/>
        </Canvas>
      </div>
  );
}

export default App;
