import React, {useRef} from 'react';
import {useFrame} from "@react-three/fiber";

interface SferaProps {
    radius: number,
    widthSegments: number,
    heightSegments: number,
    color: string,
    position?: number[]
}

export const Sfera: React.FC<SferaProps> = ({radius,widthSegments,heightSegments, color, position}) => {
    const mySphere = useRef<any>();
    useFrame(({ clock }) => {
        mySphere.current.rotation.z = clock.getElapsedTime()
     })
    return(
        <mesh position={position} ref={mySphere}>
            <sphereGeometry args={[radius,widthSegments,heightSegments]}/>
            <meshBasicMaterial color={color}/>
        </mesh>
    )

}
