import React from 'react';

interface CubeProps {
    color: string,
    x: number,
    y: number,
    z: number,
    position?: number[]
}

export const Cube: React.FC<CubeProps> = ({color, x, y, z, position}) => {
    return(
        <mesh position={position}>
            <boxGeometry args={[x,y,z]}/>
            <meshBasicMaterial color={color}/>
        </mesh>
    )
}
