import React, {MutableRefObject, useRef} from 'react';
import {useFrame} from "@react-three/fiber";

interface CubeProps {
    color: string,
    x: number,
    y: number,
    z: number,
    position?: number[],
    rotationX?: number
}

export const Cube: React.FC<CubeProps> = ({color, x, y, z, position, rotationX}) => {
    const myCube = useRef<any>();
    useFrame(({ clock }) => {
        if(rotationX !== undefined){
            myCube.current.rotation.x = rotationX
        }else{
            myCube.current.rotation.x = clock.getElapsedTime()
        }
    })
    return(
        <mesh position={position} ref={myCube}>
            <boxGeometry args={[x,y,z]}/>
            <meshBasicMaterial color={color}/>
        </mesh>
    )
}
