import React from 'react';
import {Canvas} from '@react-three/fiber';
import './App.css';
import {Cube} from "./components/cube";
import {OrbitControls, PerspectiveCamera, TransformControls} from '@react-three/drei';

function App() {


    return (
      <div id="canvas-container" style={{width: "100%", height:"100vh", backgroundColor:"black"}}>
        <Canvas style={{width: "100%", height:"100%"}}>
            <PerspectiveCamera makeDefault position={[0, 0, -10]} key={undefined} attach={undefined}
                               attachArray={undefined} attachObject={undefined} args={undefined} onUpdate={undefined}
                               visible={undefined} type={undefined} id={undefined} uuid={undefined} name={undefined}
                               parent={undefined} modelViewMatrix={undefined} normalMatrix={undefined}
                               matrixWorld={undefined} matrixAutoUpdate={undefined} matrixWorldNeedsUpdate={undefined}
                               castShadow={undefined} receiveShadow={undefined} frustumCulled={undefined}
                               renderOrder={undefined} animations={undefined} userData={undefined}
                               customDepthMaterial={undefined} customDistanceMaterial={undefined} isObject3D={undefined}
                               onBeforeRender={undefined} onAfterRender={undefined} applyMatrix4={undefined}
                               applyQuaternion={undefined} setRotationFromAxisAngle={undefined}
                               setRotationFromEuler={undefined} setRotationFromMatrix={undefined}
                               setRotationFromQuaternion={undefined} rotateOnAxis={undefined}
                               rotateOnWorldAxis={undefined} rotateX={undefined} rotateY={undefined} rotateZ={undefined}
                               translateOnAxis={undefined} translateX={undefined} translateY={undefined}
                               translateZ={undefined} localToWorld={undefined} worldToLocal={undefined}
                               lookAt={undefined} add={undefined} remove={undefined} clear={undefined}
                               getObjectById={undefined} getObjectByName={undefined} getObjectByProperty={undefined}
                               getWorldPosition={undefined} getWorldQuaternion={undefined} getWorldScale={undefined}
                               getWorldDirection={undefined} raycast={undefined} traverse={undefined}
                               traverseVisible={undefined} traverseAncestors={undefined} updateMatrix={undefined}
                               updateMatrixWorld={undefined} updateWorldMatrix={undefined} toJSON={undefined}
                               clone={undefined} copy={undefined} addEventListener={undefined}
                               hasEventListener={undefined} removeEventListener={undefined} dispatchEvent={undefined}
                               zoom={undefined} view={undefined} focus={undefined} near={undefined} far={undefined}
                               updateProjectionMatrix={undefined} setViewOffset={undefined} clearViewOffset={undefined}
                               matrixWorldInverse={undefined} projectionMatrix={undefined}
                               projectionMatrixInverse={undefined} isCamera={undefined} isPerspectiveCamera={undefined}
                               fov={undefined} aspect={undefined} filmGauge={undefined} filmOffset={undefined}
                               setFocalLength={undefined} getFocalLength={undefined} getEffectiveFOV={undefined}
                               getFilmWidth={undefined} getFilmHeight={undefined} setLens={undefined}>
                <ambientLight/>
                <pointLight position={[10, 10, 10]} />
                <OrbitControls addEventListener={undefined} hasEventListener={undefined} removeEventListener={undefined} dispatchEvent={undefined}/>
                <Cube color="orange" x={1} y={1} z={1} position={[-1,0,0]}/>
                <Cube color="hotpink" x={1} y={1} z={1} position={[1,0,0]}/>
            </PerspectiveCamera>
        </Canvas>
      </div>
  );
}

export default App;
