import React, {useRef} from 'react';
import {useFrame} from "@react-three/fiber";

interface SolarSystemProps {
    solarSystemFigli: JSX.Element[]
}

export const SolarSystem: React.FC<SolarSystemProps> = ({solarSystemFigli}) => {
    const mySolarSystem = useRef<any>();
    useFrame(({ clock }) => {
        mySolarSystem.current.rotation.z = clock.getElapsedTime()
    })
    return(
        <group ref={mySolarSystem} visible={true}>
            {solarSystemFigli?.map(figlio => {
                return figlio;
            })}
        </group>
    )

}
