import React, {useRef} from 'react';
import {useFrame} from "@react-three/fiber";

interface SferaProps {
    radius: number,
    widthSegments: number,
    heightSegments: number,
    color: string,
    position?: number[],

}

export const Sfera: React.FC<SferaProps> = (
    {radius,widthSegments,heightSegments, color, position }
)  => {
    const myObject = useRef<any>();
    useFrame(({ clock }) => {
        myObject.current.rotation.y = clock.getElapsedTime()
    })
    return(
        <mesh position={position} ref={myObject}>
            <sphereGeometry args={[radius,widthSegments,heightSegments]}/>
            <meshBasicMaterial color={color}/>
        </mesh>
    )

}
