import React, {cloneElement, createRef, useRef} from 'react';
import {useFrame} from "@react-three/fiber";

interface RotationProps {
    objectToBeRotated: JSX.Element[]
}

export const Rotation: React.FC<RotationProps> = ({objectToBeRotated}) => {
    const myObject = useRef<any>();
    const myObject2 = useRef<any>()
    useFrame(({ clock }) => {
        myObject.current.rotation.z = clock.getElapsedTime()
        myObject2.current.rotation.z = clock.getElapsedTime()
    })

    return(
        <>
            <group ref={myObject}>
                {objectToBeRotated[0]}
            </group>
            <group ref={myObject2}>
                {objectToBeRotated[0]}
            </group>
        </>


    )

}
