import React, {useRef} from 'react';
import {useFrame, useThree} from "@react-three/fiber";
import {useRotation} from "../../../hooks/useRotation";
import {map} from "react-three-gui/dist/utils";
import {useSpring, animated} from "@react-spring/web";
import {useDrag, useGesture} from "react-use-gesture";

interface SferaProps {
    radius: number,
    widthSegments: number,
    heightSegments: number,
    color: string,
    position?: number[],
    hasRotation?: boolean ,
    children?: JSX.Element[]
}

export const Sfera: React.FC<SferaProps> = (
    {radius,widthSegments,heightSegments, color, position, children, hasRotation = false }
)  => {
    const myObject = useRef<any>();

    useRotation(myObject, false, true, false, hasRotation)

    const { size, viewport } = useThree()
    const aspect = size.width / viewport.width
    const [spring, set] = useSpring(() => ({ scale: [1, 1, 1], position: [0, 0, 0], rotation: [0, 0, 0], config: { friction: 10 } }))
    const bind = useGesture({
        onDrag: ({ offset: [x, y] }) => set({ position: [x / aspect, -y / aspect, 0], rotation: [y / aspect, x / aspect, 0] }),
        onHover: ({ hovering }) => set({ scale: hovering ? [1.2, 1.2, 1.2] : [1, 1, 1] })
    })

    return(

        <mesh position={position} ref={myObject} {...bind} style={{x,y}}>
            <sphereGeometry args={[radius,widthSegments,heightSegments]}/>
            <meshToonMaterial color={color}/>
            {
                (children === undefined) ? <mesh></mesh> : children.map(child => {
                return child;
            })}
        </mesh>
    )

}
