import React from 'react';
import {Canvas} from '@react-three/fiber';
import './App.css';
import {Cube} from "./components/cube";
import {OrbitControls, PerspectiveCamera, TransformControls} from '@react-three/drei';
import {Sfera} from "./components/sfera";
import {SolarSystem} from "./components/solarSystem";
import {Rotation} from "./components/rotation";

function App() {

    const earthAndMoon = <group>
        <Sfera radius={.5} widthSegments={6} heightSegments={6} color="blue" position={[2,0,0]} />
        <Sfera radius={.2} widthSegments={6} heightSegments={6} color="grey" position={[3,0,0]} />
    </group>

    const solarSystemFigli: JSX.Element[] = [
        <Sfera radius={1} widthSegments={6} heightSegments={6} color="yellow" position={[0,0,0]} />,
        <Rotation objectToBeRotated={earthAndMoon}/>
    ]

    const solarSystem = <SolarSystem solarSystemFigli={solarSystemFigli}/>

    return (
      <div id="canvas-container" style={{width: "100%", height:"100vh", backgroundColor:"black"}}>
        <Canvas style={{width: "100%", height:"100%"}}>
            <ambientLight/>
            <pointLight position={[10, 10, 10]} />
            <OrbitControls addEventListener={undefined} hasEventListener={undefined} removeEventListener={undefined} dispatchEvent={undefined}/>
            <Rotation objectToBeRotated={solarSystem}/>

        </Canvas>
      </div>
  );
}

export default App;
